  export class MockActivityService {
    /**
     * Get all activities for a specific theme
     */
    static getAllActivitiesForTheme(themeId: string): { [categoryKey: string]: Activity[] } {
      const themeData = mockActivitiesDatabase[themeId as keyof typeof mockActivitiesDatabase]
      
      if (!themeData) {
        console.warn(`Theme ${themeId} not found in mock database`)
        return {}
      }
      
      return themeData
    }
    
    /**
     * Get activities for a specific category in a theme
     */
    static getActivitiesForCategory(themeId: string, categoryKey: string): Activity[] {
      const themeData = mockActivitiesDatabase[themeId as keyof typeof mockActivitiesDatabase]
      
      if (!themeData || !themeData[categoryKey]) {
        console.warn(`Category ${categoryKey} not found in theme ${themeId}`)
        return []
      }
      
      return themeData[categoryKey] || []
    }
    
    /**
     * Get all available themes
     */
    static getAvailableThemes(): string[] {
      return Object.keys(mockActivitiesDatabase)
    }
    
    /**
     * Get categories for a theme
     */
    static getCategoriesForTheme(themeId: string): string[] {
      const themeData = mockActivitiesDatabase[themeId as keyof typeof mockActivitiesDatabase]
      
      if (!themeData) {
        return []
      }
      
      return Object.keys(themeData)
    }
    
    /**
     * Get activity count for a category
     */
    static getCategoryCount(themeId: string, categoryKey: string): number {
      const activities = this.getActivitiesForCategory(themeId, categoryKey)
      return activities.length
    }
  }
